import React from 'react';

class Describe extends React.Component {
    render() {
        const { value, handleChange } = this.props;

        let error = undefined
        if (value.length > 120) {
            error = 'Texto muito grande!'
        }
        return (
            <label>
                    Descrição
                   <textarea
                   className='form'
                   name='describe'
                   value={ value }
                   onChange={ handleChange }
                    />
                   {/* <span>{error ? error : ''}</span> */}
                </label>
        )
    }
}

export default Describe;
